const currentYear = new Date().getFullYear();

function ensureCurrentYear(dateStr) {
  if (!dateStr) return "";

  const parts = dateStr.split("-");
  if (parts.length !== 3) return dateStr;

  const [year, month, day] = parts;

  const parsedYear = parseInt(year, 10);
  if (isNaN(parsedYear) || parsedYear < currentYear) {
    return `${currentYear}-${month}-${day}`;
  }

  return dateStr;
}

for (const item of $input.all()) {
  let raw = item.json.output;

  try {
    raw = raw.replace(/```(?:json)?\s*([\s\S]*?)\s*```/, "$1").trim();
    const parsed = JSON.parse(raw);

    // üîÅ Fix appointment year if it's in the past
    if (parsed.appointment && parsed.appointment.date) {
      parsed.appointment.date = ensureCurrentYear(parsed.appointment.date);
    }
  // üîπ Additional 24-hour / 14-day logic
    if (!parsed.appointment?.date || !parsed.appointment?.startTime) {
      parsed.isAfter24Hours = "none";
      parsed.isWithin14Days = "none";
    } else {
      const appointmentDateTime = new Date(
        `${parsed.appointment.date}T${parsed.appointment.startTime}`
      );
      const now = new Date();

      const diffHours = (appointmentDateTime - now) / (1000 * 60 * 60);
      parsed.isAfter24Hours = diffHours > 24;

      const diffDays = diffHours / 24;
      parsed.isWithin14Days = diffDays <= 14 && diffDays >= 0;
    }

    item.json = parsed;

  } catch (e) {
    item.json = {
      error: "Failed to parse output",
      original: raw
    };
  }
}

return $input.all();

